(39, 'super', '18', '03', '2018', 'Sun', '14:22', '2', 'Disperindag Kepri mengadakan Rakor Penyusunan Program dan Anggaran Tahun 2019 dengan Kabupaten / Kota di Batam', 'Rakor Penyusunan Program dan Anggaran Tahun 2019 dengan Kabupaten/Kota serta Penandatangan Kontrak TPL-IKM', 'Batam', '', '2184', '1', 44),
(40, 'super', '04', '08', '2018', 'Sat', '06:26', '1', 'Susu Kental Manis Aman Dikonsumsi, Industrinya Berkontribusi Bagi Ekonomi', '<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Kementerian Perindustrian menyatakan bahwa susu kental manis (SKM) merupakan produk yang aman untuk dikonsumsi oleh masyarakat karena sudah sesuai standar dan mendapatkan izin edar. Produk SKM masih diperlukan sebagai tambahan atau pelengkap untuk berbagai sajian kuliner, seperti pada kopi, teh tarik, atau martabak manis.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Direktur Jenderal Industri Agro, Panggah Susanto menjelaskan, SKM merupakan salah satu anasir dari berbagai macam produk turunan susu. Standar SKM diatur melalui Peraturan Kepala BPOM Nomor 21 Tahun 2016 tentang Kategori Pangan yang disusun berdasarkan standar internasional, yakni Codex Alimentarius.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Selanjutnya, berdasarkan Perka BPOM Kategori Pangan 01.3: Produk Susu Kental dan Analognya, SKM adalah produk susu berbentuk cairan kental yang diperoleh dengan menghilangkan sebagian air dari campuran susu dan gula hingga mencapai tingkat kepekatan tertentu, atau merupakan hasil rekonstitusi susu bubuk dengan penambahan gula, dengan atau tanpa penambahan bahan lain.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&ldquo;Apabila produk susu kental manis tidak memenuhi deskripsi sesuai standar tersebut maka izin edar susu kental manis tidak akan dikeluarkan oleh BPOM,&rdquo; tegas Panggah di Jakarta, Jumat (6/7).</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Lebih lanjut, mempertimbangkan keterkaitan erat antara keberlanjutan produksi SKM dengan kegiatan ekonomi masyarakat, investasi perusahaan, tenaga kerja di pabrik, dan penyerapan bahan baku susu segar dari peternak lokal, Panggah berharap pengaturan produk SKM ke depannya dapat dilakukan secara lebih bijak.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&ldquo;Hal ini dimaksudkan untuk menghindari dampak negatif terhadap iklim usaha di sektor industri penghasil SKM yang selama ini telah memberikan kontribusi signifikan bagi perekonomian nasional. Selain itu, apabila industri ini mengalami penurunan maka akan berdampak kepada puluhan ribu peternak sapi perah,&rdquo; tuturnya.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Kemenperin mencatat, seiring dengan konsumsi produk SKM yang terus naik, industrinya juga terus tumbuh berkembang. Saat ini, kapasitas produksi pabrik SKM di dalam negeri mencapai 812 ribu ton per tahun. Sementara nilai investasi di sektor usaha ini telah tembus di angka Rp5,4 triliun dengan total penyerapan tenaga kerja sebanyak 6.652 orang.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Kehadiran produk SKM di Indonesia dapat dirunut sampai pada masa pra-kemerdekaan. Pada awal mulanya, SKM masuk ke Indonesia pada tahun 1873, yaitu melalui impor SKM merek Milkmaid oleh Nestl&eacute; yang kemudian dikenal dengan nama Cap Nona dan selanjutnya pada tahun 1922 oleh De Cooperatve Condensfabriek Friesland yang sekarang dikenal dengan PT Frisian Flag Indonesia dengan produk Friesche Vlag.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Pada akhir tahun 1967, Indonesia mulai memproduksi SKM pertama kalinya melalui PT Australian Indonesian Milk atau atau yang saat ini dikenal dengan nama PT Indolakto, diikuti oleh PT Frisian Flag Indonesia pada tahun 1971 di pabriknya yang terletak di Pasar Rebo, Jakarta Timur, dan diikuti oleh PT Nestl&eacute; Indonesia pada tahun 1973 oleh pabriknya di Provinsi Jawa Timur. Setelah itu, industri SKM terus berkembang hingga sekarang.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Sumber :&nbsp;<a href=\"http://www.kemenperin.go.id/artikel/19413/Susu-Kental-Manis-Aman-Dikonsumsi,-Industrinya-Berkontribusi-Bagi-Ekonomi\">Siaran Pers Kementerian Perindustrian RI</a></span></span></span></p>\r\n', 'Jakarta', 'Kementerian Perindustrian RI', '2316', '1', 8);
