INSERT INTO `kd_kbli` (`idx`, `katid`, `kat`, `pokid`, `pok`, `golid`, `gol`, `subgolid`, `subgol`, `kelid`, `kel`, `desk`) VALUES
(1, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '101', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN DAGING', '1011', 'KEGIATAN RUMAH POTONG DAN PENGEPAKAN DAGING BUKAN UNGGAS', '10110', 'KEGIATAN RUMAH POTONG DAN PENGEPAKAN DAGING BUKAN UNGGAS', 'Kelompok ini mencakup kegiatan operasional rumah potong hewan yang berkaitan dengan kegiatan pemotongan, pengulitan, pembersihan dan pengepakan daging, seperti daging sapi, babi, biri-biri, kelinci, domba, unta dan daging segar lainnya bukan unggas, kegiatan pengurusan hasil sampingan, seperti produksi kulit dan jangat dari tempat pemotongan hewan termasuk fellmongery, penjemuran tulang, pengolahan sisaan atau kotoran hewan, penyortiran wol dan bulu dan pembersihan lemak. Termasuk kegiatan pemot'),
(2, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '101', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN DAGING', '1012', 'KEGIATAN RUMAH POTONG DAN PENGEPAKAN DAGING UNGGAS', '10120', 'KEGIATAN RUMAH POTONG DAN PENGEPAKAN DAGING UNGGAS', 'Kelompok ini mencakup kegiatan operasional rumah potong unggas dan pengepakan daging unggas, termasuk kegiatan pengurusan hasil sampingan, seperti pemrosesan sisa atau kotoran unggas, pementangan kulit, penyortiran bulu dan pembersihan lemak. Pemotongan yang dilakukan oleh pedagang dimasukkan dalam golongan 462, 472 dan 478.'),
(3, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '101', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN DAGING', '1013', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN PRODUK DAGING DAN DAGING UNGGAS', '10130', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN PRODUK DAGING DAN DAGING UNGGAS', 'Kelompok ini mencakup usaha pengolahan dan pengawetan produk daging dan daging unggas dengan cara pengalengan, pengasapan, penggaraman, pembekuan, pemanisan dan sebagainya. Kegiatannya mencakup produksi daging beku dalam bentuk carcase, produksi daging beku yang telah dipotong, produksi daging beku dalam porsi tersendiri, produksi daging yang dikeringkan, daging yang diasinkan atau daging yang diasapkan, produksi produk-produk daging, seperti sosis, salami, puding, \"andovillettes\", saveloy, bolo'),
(4, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10211', 'INDUSTRI PENGGARAMAN/PENGERINGAN IKAN', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) melalui proses penggaraman/pengeringan, seperti ikan tembang asin, ikan teri asin, dan ikan kering tawar.'),
(5, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10212', 'INDUSTRI PENGASAPAN/PEMANGGANGAN IKAN', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) melalui proses pengasapan/pemanggangan seperti ikan bandeng asap, ikan julung-julung/roa asap, ikan fufu/asar asap, ikan lele asap, dan ikan patin asap.'),
(6, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10213', 'INDUSTRI PEMBEKUAN IKAN', 'Kelompok ini mencakup usaha pengawetan ikan (bersirip/pisces) melalui proses pembekuan, seperti ikan bandeng beku, ikan tuna/cakalang beku dan kakap beku. Termasuk juga ikan utuh maupun dipotong (fillet, loin, saku, steak, chunk, brown meat) yang dibekukan. Kegiatan ini tidak termasuk usaha pendinginan ikan dengan es yang dimaksud untuk mempertahankan kesegaran ikan tersebut (10217).'),
(7, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10214', 'INDUSTRI PEMINDANGAN IKAN', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) melalui proses pemindangan, seperti pindang bandeng/paso, pindang tongkol, pindang cuwe, pindang naya, pindang lemuru/tembang, pindang layang, dan pindang cakalang. '),
(8, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10215', 'INDUSTRI PERAGIAN/FERMENTASI IKAN', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) melalui proses peragian/fermentasi, seperti peragian/fermentasi peda dan ikan kayu. '),
(9, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10216', 'INDUSTRI BERBASIS DAGING LUMATAN DAN SURIMI', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) melalui proses pelumatan daging ikan/ penggilingan. Termasuk industri daging lumat yang dicampur bahan tambahan melalui proses pemasakan atau tidak dimasak kemudian dibekukan. Contoh berbasis daging lumatan dan surimi: mata goyang, kurisi, dll. Berbasis surimi: baso, nuget, otak2, kamaboko, sosis, pempek, siomay, dimsum, chikuwa, imitation crab.'),
(10, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10217', 'INDUSTRI PENDINGINAN/PENGESAN IKAN', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) melalui proses pendinginan/pengesan.'),
(11, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1021', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN PRODUK IKAN', '10219', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN LAINNYA UNTUK IKAN', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan (bersirip/pisces) dengan cara selain yang tercakup dalam kelompok 10211 s.d. 10218. Termasuk kegiatan kapal yang digunakan hanya untuk pengolahan dan pengawetan ikan dan biota air lainnya (dalam hal ini tidak termasuk pengalengannya), produksi tepung ikan untuk konsumsi manusia dan makanan hewan dan produksi daging dan bagian dari ikan bukan untuk konsumsi manusia, konsentrat tepung ikan. '),
(12, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1022', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR DALAM KALENG', '10221', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR (BUKAN UDANG) DALAM KALENG', 'Kelompok ini mencakup usaha pengolahan dan pengawetan ikan dan biota perairan lainnya kecuali udang melalui proses pengalengan, seperti ikan sardencis dalam kaleng, kerang dalam kaleng, lemuru dalam kaleng, mackarel dalam kaleng, cephalopoda (cumi/gurita/sotong) dalam kaleng, tuna dalam kaleng, rajungan/kepiting dalam kaleng. Kegiatan kapal pengolah ikan yang hanya melakukan pengolahan dan pengawetan dalam kaleng (tanpa melakukan kegiatan penangkapan) termasuk dalam kelompok ini.'),
(13, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1022', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR DALAM KALENG', '10222', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN UDANG DALAM KALENG', 'Kelompok ini mencakup usaha pengolahan dan pengawetan udang melalui proses pengalengan (udang dalam kaleng). Kegiatan kapal pengolah udang yang hanya melakukan pengolahan dan pengawetan dalam kaleng (tanpa melakukan kegiatan penangkapan) termasuk dalam kelompok ini.'),
(14, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10291', 'INDUSTRI PENGGARAMAN/PENGERINGAN BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan crustacea, mollusca dan biota perairan lainnya melalui proses penggaraman/pengeringan, seperti udang asin, cumi-cumi asin, ebi, ubur-ubur asin, sotong asin, teripang kering, sotong kering, dan lainnya.'),
(15, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10292', 'INDUSTRI PENGASAPAN/PEMANGGANGAN BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan crustacea, mollusca dan biota perairan lainnya melalui proses pengasapan/pemanggangan, seperti sotong asap/panggang, teripang asap/panggang.'),
(16, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10293', ' INDUSTRI PEMBEKUAN BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengawetan crustacea, mollusca dan biota perairan lainnya melalui proses pembekuan, seperti udang beku, paha kodok beku, cephalopoda (cumi/sotong/gurita) beku, kepiting/rajungan beku, dan kerang beku. Kegiatan ini tidak termasuk usaha pendinginan crustacea, mollusca dan biota air lainnya dengan es yang dimaksud untuk mempertahankan kesegarannya (10297). '),
(17, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10294', ' INDUSTRI PEMINDANGAN BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan crustacea, mollusca dan biota perairan lainnya melalui proses pemindangan.'),
(18, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10295', 'INDUSTRI PERAGIAN/FERMENTASI BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan crustacea, mollusca dan biota perairan lainnya melalui proses peragian/fermentasi.'),
(19, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10296', 'INDUSTRI BERBASIS LUMATAN BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan biota air lainnya melalui proses pelumatan daging/penggilingan/pencampuran bahan tambahan/pengukusan, seperti lumatan cumi, lumatan udang, baso udang, baso cumi, baso kepiting, dan kaki naga udang.'),
(20, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10297', 'INDUSTRI PENDINGINAN/PENGESAN BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan crustacea, mollusca dan biota perairan lainnya melalui proses pendinginan/pengesan, seperti cumi segar, kerang segar, teripang segar, dan kepiting segar.'),
(21, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10298', 'INDUSTRI PENGOLAHAN RUMPUT LAUT', 'Kelompok ini mencakup usaha pengolahan rumput laut menjadi rumput laut kering olahan (alkali treated caragenan chips), gelatin, agar-agar, karagenan dan lainnya. '),
(22, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '102', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN IKAN DAN BIOTA AIR', '1029', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BIOTA AIR LAINNYA', '10299', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN LAINNYA UNTUK BIOTA AIR LAINNYA', 'Kelompok ini mencakup usaha pengolahan dan pengawetan crustacea, mollusca dan biota perairan lainnya dengan cara selain yang tercakup dalam kelompok 10291 s.d. 10298, seperti tepung udang, tepung kerang, dan tepung kodok. '),
(23, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1031', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN DENGAN CARA DIASINKAN, DILUMATKAN, DIKERINGKAN DAN DIBEKUKAN', '10311', 'INDUSTRI PENGASINAN/PEMANISAN BUAH-BUAHAN DAN SAYURAN', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran dengan proses pengasinan/pemanisan, baik dalam bentuk kemasan ataupun tidak, seperti asinan kedondong, asinan wortel, manisan pala dan manisan mangga. '),
(24, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1031', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN DENGAN CARA DIASINKAN, DILUMATKAN, DIKERINGKAN DAN DIBEKUKAN', '10312', 'INDUSTRI PELUMATAN BUAH-BUAHAN DAN SAYURAN', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran dengan proses pelumatan, baik dalam bentuk kemasan ataupun tidak, seperti selai mangga, jelly murbai, dan cabe giling.'),
(25, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1031', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN DENGAN CARA DIASINKAN, DILUMATKAN, DIKERINGKAN DAN DIBEKUKAN', '10313', 'INDUSTRI PENGERINGAN BUAH-BUAHAN DAN SAYURAN', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran dengan cara pengeringan, baik dalam bentuk kemasan ataupun tidak, seperti kismis (anggur), bawang merah, bawang putih, cabe kering, rebung kering dan jamur kering.'),
(26, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1031', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN DENGAN CARA DIASINKAN, DILUMATKAN, DIKERINGKAN DAN DIBEKUKAN', '10314', 'INDUSTRI PEMBEKUAN BUAH-BUAHAN DAN SAYURAN', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran dengan proses pembekuan, seperti buah-buahan beku dan sayur-sayuran beku.'),
(27, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1032', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN DALAM KALENG', '10320', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN DALAM KALENG', 'Kelompok ini mencakup usaha pengolahan dan pengawetan buah-buahan dan sayuran melalui proses pengalengan, seperti nanas dalam kaleng, rambutan dalam kaleng, kacang dalam kaleng dan wortel dalam kaleng. Yang dimaksud pengalengan di sini merupakan proses pengawetan dan bukan hanya pengemasan. '),
(28, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1033', 'INDUSTRI PENGOLAHAN SARI BUAH DAN SAYURAN', '10330', 'INDUSTRI PENGOLAHAN SARI BUAH DAN SAYURAN', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran dengan cara pengolahan sari buah-buahan dan sayuran, seperti bubuk sari buah-buahan, air/sari pekat buah buahan dan air/sari pekat sayuran.'),
(29, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1039', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN LAINNYA BUAH-BUAHAN DAN SAYURAN', '10391', 'INDUSTRI TEMPE KEDELAI', 'Kelompok ini mencakup usaha pembuatan tempe dari kedelai. Usaha pembuatan tempe yang bahan bakunya selain kedelai, seperti tempe bongkrek, dimasukkan dalam kelompok 10399.'),
(30, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1039', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN LAINNYA BUAH-BUAHAN DAN SAYURAN', '10392', 'INDUSTRI TAHU KEDELAI', 'Kelompok ini mencakup usaha pembuatan tahu dari kedelai.'),
(31, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '103', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN BUAH-BUAHAN DAN SAYURAN', '1039', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN LAINNYA BUAH-BUAHAN DAN SAYURAN', '10399', 'INDUSTRI PENGOLAHAN DAN PENGAWETAN LAINNYA BUAH-BUAHAN DAN SAYURAN', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran dengan cara selain yang tercakup dalam subgolongan 1031 s.d. 1033, seperti industri pengupasan kentang dan produksi konsentrat dari buah dan sayuran segar.'),
(32, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1041', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI (BUKAN KELAPA DAN KELAPA SAWIT)', '10411', 'INDUSTRI MINYAK MENTAH DAN LEMAK NABATI', 'Kelompok ini mencakup usaha pengolahan bahan-bahan dari nabati menjadi minyak mentah (crude oil) yang masih perlu diolah lebih lanjut dan biasanya produk ini dipakai oleh industri lain (kecuali minyak mentah kelapa sawit (crude plam oil) dan minyak mentah kelapa) termasuk juga industri hasil lemak dari nabati yang dapat digunakan sebagai bahan makanan, seperti minyak bunga matahari.'),
(33, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1041', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI (BUKAN KELAPA DAN KELAPA SAWIT)', '10412', 'INDUSTRI MARGARINE', 'Kelompok ini mencakup usaha pembuatan margarine dari minyak makan nabati.'),
(34, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1041', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI (BUKAN KELAPA DAN KELAPA SAWIT)', '10413', 'INDUSTRI MINYAK MENTAH DAN LEMAK HEWANI SELAIN IKAN', 'Kelompok ini mencakup usaha pengolahan bahan-bahan dari hewani selain ikan menjadi minyak mentah dan lemak hewani, seperti minyak/lemak babi, lemak sapi dan lemak unggas.'),
(35, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1041', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI (BUKAN KELAPA DAN KELAPA SAWIT)', '10414', 'INDUSTRI MINYAK IKAN', 'Kelompok ini mencakup usaha pembuatan minyak yang berbahan baku dari badan ikan yang berlemak tinggi, seperti lemuru, atau dari organ badan ikan seperti hati cucut. Mencakup juga industri minyak yang dihasilkan dari hasil sampingan pengalengan ikan, seperti hasil pengalengan sarden. Pengolahan minyak ikan/biota perairan lainnya yang digunakan untuk bahan farmasi ataupun kosmetik dimasukkan dalam subgolongan 2101 dan 2102.'),
(36, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1041', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI (BUKAN KELAPA DAN KELAPA SAWIT)', '10415', 'INDUSTRI MINYAK GORENG BUKAN MINYAK KELAPA DAN MINYAK KELAPA SAWIT', 'Kelompok ini mencakup usaha pembuatan minyak goreng lainnya, bukan minyak goreng kelapa dan minyak goreng kelapa sawit, seperti minyak bekatul, minyak goreng babi dan minyak goreng unggas.'),
(37, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1042', 'INDUSTRI KOPRA, MINYAK MENTAH DAN MINYAK GORENG KELAPA, TEPUNG DAN PELET KELAPA', '10421', 'INDUSTRI KOPRA', 'Kelompok ini mencakup usaha pembuatan kopra.'),
(38, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1042', 'INDUSTRI KOPRA, MINYAK MENTAH DAN MINYAK GORENG KELAPA, TEPUNG DAN PELET KELAPA', '10422', 'INDUSTRI MINYAK MENTAH KELAPA', 'Kelompok ini mencakup usaha pengolahan kelapa menjadi minyak mentah (crude oil) yang masih perlu diolah lebih lanjut dan biasanya produk ini dipakai oleh industri lain.'),
(39, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1042', 'INDUSTRI KOPRA, MINYAK MENTAH DAN MINYAK GORENG KELAPA, TEPUNG DAN PELET KELAPA', '10423', 'INDUSTRI MINYAK GORENG KELAPA', 'Kelompok ini mencakup usaha pengolahan lebih lanjut (pemurnian, pemucatan dan penghilangan bau yang tidak dikehendaki) dari minyak mentah kelapa menjadi minyak goreng kelapa.'),
(40, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1042', 'INDUSTRI KOPRA, MINYAK MENTAH DAN MINYAK GORENG KELAPA, TEPUNG DAN PELET KELAPA', '10424', 'INDUSTRI TEPUNG DAN PELET KELAPA', 'Kelompok ini mencakup usaha pengolahan lebih lanjut kelapa, seperti tepung dan pelet kelapa.'),
(41, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10431', 'INDUSTRI MINYAK MENTAH KELAPA SAWIT (CRUDE PALM OIL)', 'Kelompok ini mencakup usaha pengolahan kelapa sawit menjadi minyak mentah (Crude Palm Oil/CPO) yang masih perlu diolah lebih lanjut dan biasanya produk ini dipakai oleh industri lain.'),
(42, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10432', 'INDUSTRI MINYAK MENTAH INTI KELAPA SAWIT (CRUDE PALM KERNEL OIL)', 'Kelompok ini mencakup usaha pengolahan inti kelapa sawit menjadi minyak mentah inti (Crude Palm Kernel Oil/CPKO) yang masih perlu diolah lebih lanjut dan biasanya produk ini dipakai oleh industri lain.'),
(43, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10433', 'INDUSTRI PEMISAHAN/FRAKSINASI MINYAK MENTAH KELAPA SAWIT DAN MINYAK MENTAH INTI KELAPA SAWIT', 'Kelompok ini mencakup pemisahan fraksi padat dan fraksi cair dari minyak mentah kelapa sawit menjadi minyak mentah kelapa sawit olein (Crude Palm Olein) dan minyak mentah kelapa sawit stearin (Crude Palm Stearin) atau dari minyak mentah inti kelapa sawit menjadi minyak mentah inti kelapa sawit olein (Crude Palm Kernel Olein) dan minyak mentah inti kelapa sawit stearin (Crude Palm Kernel Stearin) yang masih perlu diolah lebih lanjut.'),
(44, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10434', 'INDUSTRI PEMURNIAN MINYAK MENTAH KELAPA SAWIT DAN MINYAK MENTAH INTI KELAPA SAWIT', 'Kelompok ini mencakup pemurnian minyak mentah dari kelapa sawit menjadi minyak murni kelapa sawit (Refined Bleached Deodorized Palm Oil) atau dari minyak inti kelapa sawit menjadi minyak murni inti kelapa sawit (Refined Bleached Deodorized Palm Kernel Oil) yang masih perlu diolah lebih lanjut.'),
(45, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10435', 'INDUSTRI PEMISAHAN/FRAKSINASI MINYAK MURNI KELAPA SAWIT', 'Kelompok ini mencakup usaha pemisahan fraksi padat dan fraksi cair dari minyak murni kelapa sawit menjadi miyak murni kelapa sawit olein (Refined Bleached Deodorized Palm Olein) dan minyak murni kelapa sawit stearin (Refined Bleached Deodorized Palm Stearin). '),
(46, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10436', 'INDUSTRI PEMISAHAN/FRAKSINASI MINYAK MURNI INTI KELAPA SAWIT', 'Kelompok ini mencakup usaha pemisahan fraksi padat dan fraksi cair dari minyak murni inti kelapa sawit menjadi minyak murni inti kelapa sawit olein (Refined Bleached eodorized Palm Kernel Olein) dan miyak murni inti kelapa sawit stearin (Refined Bleached Deodorized Palm Kernel Stearin).'),
(47, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1043', 'INDUSTRI MINYAK MENTAH/MURNI KELAPA SAWIT (CRUDE PALM OIL) DAN MINYAK GORENG KELAPA SAWIT', '10437', 'INDUSTRI MINYAK GORENG KELAPA SAWIT', 'Kelompok ini mencakup usaha pengolahan lebih lanjut (pemurnian, pemucatan dan penghilangan bau yang tidak dikehendaki) dari minyak mentah kelapa sawit (CPO) menjadi minyak goreng kelapa sawit.'),
(48, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '104', 'INDUSTRI MINYAK DAN LEMAK NABATI DAN HEWANI', '1049', 'INDUSTRI MINYAK MENTAH DAN LEMAK NABATI DAN HEWANI LAINNYA', '10490', 'INDUSTRI MINYAK MENTAH DAN LEMAK NABATI DAN HEWANI LAINNYA', 'Kelompok ini mencakup usaha pengolahan lainnya untuk minyak dan lemak, yang belum tercakup pada subgolongan 1041 s.d. 1043, seperti industri shorterning (minyak roti), industri minyak dan lemak dari binatang yang tidak dapat dimakan, produksi (linter) sisaan kapas, bungkil atau ampas dan hasil sisaan lainnya dari produksi minyak dan penyulingan minyak dari ikan dan mamalia laut.'),
(49, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '105', 'INDUSTRI PENGOLAHAN SUSU, PRODUK DARI SUSU DAN ES KRIM', '1051', 'INDUSTRI PENGOLAHAN SUSU SEGAR DAN KRIM', '10510', 'INDUSTRI PENGOLAHAN SUSU SEGAR DAN KRIM', 'Kelompok ini mencakup usaha Iidustri pengolahan susu cair segar, susu dipasteurisasi, disterilisasi, homogenisasi dan atau pemanasan ultra (UHT) dan industri pengolahan krim dari susu cair segar, pasteurisasi, sterilisasi dan homogenisasi.'),
(50, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '105', 'INDUSTRI PENGOLAHAN SUSU, PRODUK DARI SUSU DAN ES KRIM', '1052', 'INDUSTRI PENGOLAHAN SUSU BUBUK DAN SUSU KENTAL', '10520', 'INDUSTRI PENGOLAHAN SUSU BUBUK DAN SUSU KENTAL', 'Kelompok ini mencakup usaha industri pengolahan susu bubuk atau susu kental dengan pemanis atau tidak dan industri pengolahan susu atau krim dalam bentuk yang padat.'),
(51, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '105', 'INDUSTRI PENGOLAHAN SUSU, PRODUK DARI SUSU DAN ES KRIM', '1053', 'INDUSTRI PENGOLAHAN ES KRIM DAN SEJENISNYA', '10531', 'INDUSTRI PENGOLAHAN ES KRIM', 'Kelompok ini mencakup usaha pembuatan berbagai macam es krim yang bahan utamanya dari susu. Pembuatan es krim yang bahan utamanya bukan dari susu dimasukkan dalam kelompok 10532.'),
(52, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '105', 'INDUSTRI PENGOLAHAN SUSU, PRODUK DARI SUSU DAN ES KRIM', '1053', 'INDUSTRI PENGOLAHAN ES KRIM DAN SEJENISNYA', '10532', 'INDUSTRI PENGOLAHAN ES SEJENISNYA YANG DAPAT DIMAKAN (BUKAN ES BATU DAN ES BALOK)', 'Kelompok ini mencakup usaha pembuatan berbagai macam es yang bahan utamanya bukan dari susu, seperti sorbet, es lilin, ice drop, es dengan berbagai rasa lainnya, es mambo dan es puter. Usaha es kering (dry ice) dimasukkan dalam kelompok 20112.'),
(53, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '105', 'INDUSTRI PENGOLAHAN SUSU, PRODUK DARI SUSU DAN ES KRIM', '1059', 'INDUSTRI PENGOLAHAN PRODUK DARI SUSU LAINNYA', '10590', 'INDUSTRI PENGOLAHAN PRODUK DARI SUSU LAINNYA', 'Kelompok ini mencakup usaha pengolahan produk dari susu lainnya, seperti mentega, yoghurt, keju dan dadih, air dadih, kaseinatau laktosa (susu manis) dan bubuk es krim. Pembuatan es krim yang bahan utamanya dari susu dimasukkan dalam kelompok 10531.'),
(54, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1061', 'INDUSTRI PENGGILINGAN SERELIA DAN BIJI-BIJIAN LAINNYA (BUKAN BERAS DAN JAGUNG)', '10611', 'INDUSTRI PENGGILINGAN GANDUM DAN SERELIA LAINNYA', 'Kelompok ini mencakup usaha penggilingan gandum dan serelia lainnya menjadi tepung dan pelet , seperti gandum dan sorghum, rye, oat dan serelia lainnya.'),
(55, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1061', 'INDUSTRI PENGGILINGAN SERELIA DAN BIJI-BIJIAN LAINNYA (BUKAN BERAS DAN JAGUNG)', '10612', 'INDUSTRI PENGGILINGAN ANEKA KACANG (TERMASUK LEGUMINOUS)', 'Kelompok ini mencakup usaha pembuatan tepung dari aneka kacang melalui proses penggilingan, seperti tepung kacang hijau, tepung kacang kedelai dan tepung lainnya (seperti dari kacang tanah, kacang merah dan tanaman leguminous lainnya).'),
(56, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1061', 'INDUSTRI PENGGILINGAN SERELIA DAN BIJI-BIJIAN LAINNYA (BUKAN BERAS DAN JAGUNG)', '10613', 'INDUSTRI PENGGILINGAN ANEKA UMBI DAN SAYURAN (TERMASUK RHIZOMA)', 'Kelompok ini mencakup usaha pembuatan tepung dari aneka umbi dan sayuran melalui proses penggilingan, seperti tepung dari ubi kayu (gaplek), ubi jalar, talas, irut, jahe, temulawak, kunyit dan kapulaga dan sayuran. '),
(57, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1061', 'INDUSTRI PENGGILINGAN SERELIA DAN BIJI-BIJIAN LAINNYA (BUKAN BERAS DAN JAGUNG)', '10614', 'INDUSTRI TEPUNG CAMPURAN DAN ADONAN TEPUNG', 'Kelompok ini mencakup usaha pembuatan tepung campuran dan adonan tepung yang sudah dicampur untuk roti, kue, biskuit, kue dadar.'),
(58, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1061', 'INDUSTRI PENGGILINGAN SERELIA DAN BIJI-BIJIAN LAINNYA (BUKAN BERAS DAN JAGUNG)', '10615', 'INDUSTRI MAKANAN SEREAL', 'Kelompok ini mencakup usaha pembuatan makanan sereal.'),
(59, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1061', 'INDUSTRI PENGGILINGAN SERELIA DAN BIJI-BIJIAN LAINNYA (BUKAN BERAS DAN JAGUNG)', '10616', 'INDUSTRI TEPUNG TERIGU', 'Kelompok ini mencakup usaha pembuatan tepung terigu'),
(60, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1062', 'INDUSTRI PATI DAN PRODUK PATI (BUKAN BERAS DAN JAGUNG)', '10621', 'INDUSTRI PATI UBI KAYU', 'Kelompok ini mencakup usaha pembuatan pati ubi kayu melalui ekstraksi, seperti tepung tapioka.'),
(61, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1062', 'INDUSTRI PATI DAN PRODUK PATI (BUKAN BERAS DAN JAGUNG)', '10622', 'INDUSTRI BERBAGAI MACAM PATI PALMA', 'Kelompok ini mencakup usaha pembuatan pati dari berbagai macam tanaman suku palma, seperti pati sagu dan pati aren.'),
(62, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1062', 'INDUSTRI PATI DAN PRODUK PATI (BUKAN BERAS DAN JAGUNG)', '10623', 'INDUSTRI GLUKOSA DAN SEJENISNYA', 'Kelompok ini mencakup usaha pembuatan gula glukosa, selain yang termasuk dalam kelompok 10721 dan 10722, seperti glucosa, fructosa, lactosa, maltosa, sacharosa dan gula stevia.'),
(63, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1062', 'INDUSTRI PATI DAN PRODUK PATI (BUKAN BERAS DAN JAGUNG)', '10629', 'INDUSTRI PATI LAINNYA', 'Kelompok ini mencakup usaha pembuatan berbagai macam pati melalui proses ekstraksi yang belum termasuk kelompok 10621 s.d. 10623, seperti pati kentang, pati bengkoang, pati temulawak, pati irut dan pati biji mangga. '),
(64, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1063', 'INDUSTRI PENGGILINGAN BERAS DAN JAGUNG DAN INDUSTRI TEPUNG BERAS DAN JAGUNG', '10631', 'INDUSTRI PENGGILINGAN PADI DAN PENYOSOHAN BERAS', 'Kelompok ini mencakup usaha penggilingan padi menjadi beras, termasuk penyosohan beras yang terpisah dengan usaha penggilingan padi.'),
(65, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1063', 'INDUSTRI PENGGILINGAN BERAS DAN JAGUNG DAN INDUSTRI TEPUNG BERAS DAN JAGUNG', '10632', 'INDUSTRI PENGGILINGAN DAN PEMBERSIHAN JAGUNG', 'Kelompok ini mencakup usaha penggilingan dan pembersihan jagung.'),
(66, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1063', 'INDUSTRI PENGGILINGAN BERAS DAN JAGUNG DAN INDUSTRI TEPUNG BERAS DAN JAGUNG', '10633', 'INDUSTRI TEPUNG BERAS DAN TEPUNG JAGUNG', 'Kelompok ini mencakup usaha pembuatan tepung beras dan tepung jagung.'),
(67, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '106', 'INDUSTRI PENGGILINGAN PADI-PADIAN, TEPUNG DAN PATI', '1063', 'INDUSTRI PENGGILINGAN BERAS DAN JAGUNG DAN INDUSTRI TEPUNG BERAS DAN JAGUNG', '10634', 'INDUSTRI PATI BERAS DAN JAGUNG', 'Kelompok ini mencakup usaha pembuatan pati beras dan pati jagung (maizena).'),
(68, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1071', 'INDUSTRI PRODUK ROTI DAN KUE', '10710', 'INDUSTRI PRODUK ROTI DAN KUE', 'Kelompok ini mencakup usaha pembuatan berbagai macam roti dan kue, seperti industri roti tawar dan roti kadet; industri kue, pie, tart; industri biskuit dan produk roti kering lainnya; industri pengawetan kue kering dan cake; industri produk makanan ringan (cookies, cracker, kue kering) baik yang manis atau asin; industri tortillas; dan industri produk roti yang dibekukan, seperti pancake, waffle dan roti kadet.'),
(69, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1072', 'INDUSTRI GULA', '10721', 'INDUSTRI GULA PASIR', 'Kelompok ini mencakup usaha pembuatan gula yang berbentuk kristal (pasir), bahan utamanya dari tebu, bit ataupun lainnya.'),
(70, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1072', 'INDUSTRI GULA', '10722', 'INDUSTRI GULA MERAH', 'Kelompok ini mencakup usaha pembuatan gula merah yang tidak berbentuk kristal, dengan bahan utamanya tebu maupun nira (aren, kelapa dan sejenisnya).'),
(71, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1072', 'INDUSTRI GULA', '10723', 'INDUSTRI SIROP', 'Kelompok ini mencakup usaha pengolahan gula menjadi sirop, seperti industri sirup gula dan produksi sirup dan gula maple. Kegiatan pembuatan sirop yang tergabung dengan pabrik gula dan tidak dapat dipisahkan tersendiri dimasukkan dalam kelompok 10721 atau 10722.'),
(72, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1072', 'INDUSTRI GULA', '10729', 'INDUSTRI PENGOLAHAN GULA LAINNYA BUKAN SIROP', 'Kelompok ini mencakup usaha pengolahan gula ke dalam bentuk lain, termasuk pembuatan gula batu, tepung gula, gula pengganti dari jus tebu, bit, maple dan kelapa, nira, aren dan molasse (harum manis). '),
(73, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1073', 'INDUSTRI KAKAO, COKELAT DAN KEMBANG GULA', '10731', 'INDUSTRI KAKAO', 'Kelompok ini mencakup usaha pengolahan biji kakao menjadi bubuk kakao, lemak kakao, pasta kakao, dan bungkil kakao.'),
(74, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1073', 'INDUSTRI KAKAO, COKELAT DAN KEMBANG GULA', '10732', 'INDUSTRI MAKANAN DARI COKELAT DAN KEMBANG GULA', 'Kelompok ini mencakup usaha pembuatan segala macam makanan yang bahan utamanya dari cokelat seperti cokelat dan gula-gula dari cokelat dan pembuatan segala macam kembang gula seperti caramel, cachous, nougat, fondant dan cokelat putih. Termasuk industri minuman dari cokelat dalam bentuk bubuk maupun cair.'),
(75, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1073', 'INDUSTRI KAKAO, COKELAT DAN KEMBANG GULA', '10733', 'INDUSTRI MANISAN BUAH-BUAHAN DAN SAYURAN KERING', 'Kelompok ini mencakup usaha pengawetan buah-buahan dan sayuran baik buah, kacang, kulit buah dan bagian lain dari tumbuhan dengan proses pengasinan/pemanisan dan pengeringan, baik dalam bentuk kemasan ataupun tidak, seperti manisan pala dan manisan mangga kering, sayuran dan buah-buahan kering lainnya.'),
(76, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1073', 'INDUSTRI KAKAO, COKELAT DAN KEMBANG GULA', '10739', 'INDUSTRI KEMBANG GULA LAINNYA', 'Kelompok ini mencakup usaha industri kembang gula lainnya, seperti permen karet dan permen obat batuk dan pastilles.'),
(77, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1074', 'INDUSTRI MAKARONI, MIE DAN PRODUK SEJENISNYA', '10740', 'INDUSTRI MAKARONI, MIE DAN PRODUK SEJENISNYA', 'Kelompok ini mencakup usaha pembuatan makaroni, mie, spagheti, bihun, so`un dan sejenisnya, baik dimasak atau tidak dalam bentuk basah maupun kering. Termasuk industri couscous dan industri produk pasta yang dibekukan atau dikalengkan.'),
(78, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1075', 'INDUSTRI MAKANAN DAN MASAKAN OLAHAN', '10750', 'INDUSTRI MAKANAN DAN MASAKAN OLAHAN', 'Kelompok ini mencakup industri makanan siap saji (diolah, dibumbui dan dimasak) diolah untuk tujuan diawetkan dalam kaleng atau dibekukan dan biasanya dikemas dan dilabel untuk dijual kembali. Mencakup juga industri masakan daging atau unggas, industri masakan ikan (kecuali masakan ikan dan udang dalam kaleng), industri masakan sayuran siap saji, industri masakan rebusan dalam kaleng dan makanan di dalam wadah hampa udara dan industri masakan siap saji yang lain. Termasuk industri pizza beku, pe'),
(79, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1076', 'INDUSTRI PENGOLAHAN KOPI, TEH DAN HERBAL (HERB INFUSION)', '10761', 'INDUSTRI PENGOLAHAN KOPI', 'Kelompok ini mencakup usaha penyangraian, penggilingan dan pensarian (ekstraksi) kopi menjadi berbagai macam bubuk atau cairan, seperti kopi sangrai, kopi bubuk, kopi instan, ekstrak dan sari kopi. Termasuk industri pengganti pengganti. Penggilingan kopi bubuk di tempat pedagang kopi dimasukkan dalam kelompok 47222 dan 47823.'),
(80, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1076', 'INDUSTRI PENGOLAHAN KOPI, TEH DAN HERBAL (HERB INFUSION)', '10762', 'INDUSTRI PENGOLAHAN HERBAL (HERB INFUSION)', 'Kelompok ini mencakup usaha pengolahan herbal (mint, vervain, chamomil). Termasuk industri makanan suplemen dari herbal.'),
(81, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1076', 'INDUSTRI PENGOLAHAN KOPI, TEH DAN HERBAL (HERB INFUSION)', '10763', 'INDUSTRI PENGOLAHAN TEH', 'Kelompok ini mencakup usaha pengolahan daun teh menjadi teh. Termasuk kegiatan pencampuran teh dan mate, industri ekstraksi dan olahan berbahan dasar teh dan mate.'),
(82, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1077', 'INDUSTRI BUMBU-BUMBUAN DAN PRODUK MASAK LAINNYA', '10771', 'INDUSTRI KECAP', 'Kelompok ini mencakup usaha pembuatan kecap dari kedele/kacang-kacangan lainnya, termasuk kecap ikan dan pembuatan tauco (baik dari kedelai/kacang-kacangan lainnya yang masih segar, maupun dari hasil sisa pembuatan kecap).'),
(83, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1077', 'INDUSTRI BUMBU-BUMBUAN DAN PRODUK MASAK LAINNYA', '10772', 'INDUSTRI BUMBU MASAK DAN PENYEDAP MASAKAN', 'Kelompok ini mencakup usaha pembuatan bumbu masak dalam keadaan sudah diramu atau belum, baik berbentuk bubuk ataupun lainnya, seperti bumbu gulai, bumbu kari, bumbu merica, bubuk jahe, bubuk jinten, bubuk pala, bubuk cabe dan bubuk kayu manis. Termasuk usaha industri penyedap masakan baik yang asli, natura maupun sintesa khemis, seperti vetsin dan serbuk panili dan industri bumbu-bumbu, saus dan rempah-rempah, seperti mayonais, tepung mustar, mustar olahan, sauce tomat, dan sauce selada.'),
(84, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1077', 'INDUSTRI BUMBU-BUMBUAN DAN PRODUK MASAK LAINNYA', '10773', 'INDUSTRI PRODUK MASAK DARI KELAPA', 'Kelompok ini mencakup usaha pembuatan produk masak dari kelapa yang belum tercakup dalam golongan manapun, seperti santan pekat dan santan cair, kecap kelapa, sari kelapa (nata de coco), kelapa parut kering (dicicated coconut) dan krim kelapa.'),
(85, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1077', 'INDUSTRI BUMBU-BUMBUAN DAN PRODUK MASAK LAINNYA', '10774', 'INDUSTRI PENGOLAHAN GARAM', 'Kelompok ini mencakup usaha pengolahan garam dapur.'),
(86, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1077', 'INDUSTRI BUMBU-BUMBUAN DAN PRODUK MASAK LAINNYA', '10779', 'INDUSTRI PRODUK MASAK LAINNYA', 'Kelompok ini mencakup usaha pembuatan petis, saus tiram, terasi, khitin/khitosan, atau yang sejenisnya, dengan bahan baku utamanya ikan dan udang ataupun bagian-bagiannya, industri madu dan karamel buatan, industri ekstraksi dan jus dari daging dan ikan, industri konsentrat buatan/pembuatan macam-macam makanan yang belum tercakup dalam golongan manapun, seperti cincau, gist, baking powder, essence dan cuka makan. Termasuk usaha pembuatan tempe dari kacang-kacangan lainnya dan oncom (dari kacang '),
(87, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1079', 'INDUSTRI PRODUK MAKANAN LAINNYA', '10791', 'INDUSTRI MAKANAN BAYI', 'Kelompok ini mencakup usaha pembuatan makanan bayi, seperti formula bayi, susu lanjutan dan makanan lanjutan lainnya, makanan bayi dan makanan yang mengandung bahan yang dihomogenisasi.'),
(88, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1079', 'INDUSTRI PRODUK MAKANAN LAINNYA', '10792', 'INDUSTRI KUE BASAH', 'Kelompok ini mencakup usaha pembuatan macam-macam makanan kue basah, yaitu sejenis kue yang relatif tidak tahan lama, seperti wajik, lemper, kue lapis, lumpia, dan martabak (termasuk pembuatan tape dan dodol).'),
(89, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1079', 'INDUSTRI PRODUK MAKANAN LAINNYA', '10793', 'INDUSTRI MAKANAN DARI KEDELE DAN KACANG-KACANGAN LAINNYA BUKAN KECAP, TEMPE DAN TAHU', 'Kelompok ini mencakup usaha pembuatan makanan dari kedele/kacang-kacangan lainnya bukan kecap dan tempe, seperti keripik/peyek dari kacang-kacangan, daging sintetis, kacang kapri, kacang asin, kacang telur, kacang sukro, kacang bogor, kacang atom, kacang mete dan enting-enting.'),
(90, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1079', 'INDUSTRI PRODUK MAKANAN LAINNYA', '10794', 'INDUSTRI KERUPUK, KERIPIK, PEYEK DAN SEJENISNYA', 'Kelompok ini mencakup usaha industri berbagai macam kerupuk, keripik, peyek dan sejenisnya, seperti kerupuk udang, kerupuk ikan dan kerupuk pati (kerupuk terung). Dan usaha pembuatan berbagai macam makanan sejenis kerupuk, seperti macam-macam emping, kecimpring, karak, gendar, opak, keripik paru, keripik bekicot dan keripik kulit, peyek teri, peyek udang. Kegiatan atau usaha pembuatan keripik/peyek dari kacang kacangan dimasukkan dalam kelompok 10793.'),
(91, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1079', 'INDUSTRI PRODUK MAKANAN LAINNYA', '10795', 'INDUSTRI KRIMER NABATI', 'Kelompok ini mencakup usaha pembuatan krimer nabati emulsi lemak nabati yang berasal dari kelapa atau kelapa sawit yang digunakan sebagai campuran makanan atau minuman.'),
(92, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '107', 'INDUSTRI MAKANAN LAINNYA', '1079', 'INDUSTRI PRODUK MAKANAN LAINNYA', '10799', 'INDUSTRI PRODUK MAKANAN LAINNYA', 'Kelompok ini mencakup usaha pembuatan produk makanan lainnya, seperti telur asin, soup dan kaldu, makanan yang tidak tahan lama, seperti sandwich, pizza mentah dan lainnya. Termasuk industri ragi, susu dan keju pengganti dari selain susu dan produk telur dan albumin telur.'),
(93, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '108', 'INDUSTRI MAKANAN HEWAN', '1080', 'INDUSTRI MAKANAN HEWAN', '10801', 'INDUSTRI RANSUM MAKANAN HEWAN', 'Kelompok ini mencakup usaha pembuatan berbagai macam ransum pakan ternak, unggas, ikan dan hewan lainnya.'),
(94, 'C', 'INDUSTRI PENGOLAHAN', '10', 'INDUSTRI MAKANAN', '108', 'INDUSTRI MAKANAN HEWAN', '1080', 'INDUSTRI MAKANAN HEWAN', '10802', 'INDUSTRI KONSENTRAT MAKANAN HEWAN', 'Kelompok ini mencakup usaha pembuatan konsentrat pakan ternak, unggas dan hewan lainnya. Pengolahan konsentrat pakan ternak, unggas dan hewan lainnya yang tidak dapat di pisahkan dari usaha peternakan dimasukkan dalam golongan 014 (Peternakan).'),
(95, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1101', 'INDUSTRI MINUMAN BERALKOHOL HASIL DESTILASI', '11010', 'INDUSTRI MINUMAN BERALKOHOL HASIL DESTILASI', 'Kelompok ini mencakup industri pengolahan minuman yang menggunakan bahan baku alkohol (ethyl alcohol) dengan proses destilasi dengan kadar 20%-55%, seperti whisky, brandy, rum, gin, liqueurs, tidak termasuk residu sulphite dari pabrik pulp dan pencampuran minuman beralkohol, termasuk produk minuman beralkohol netral (tanpa rasa/flavor). Industri alkohol murni dimasukkan dalam kelompok 20115. Usaha pembotolan saja, tanpa melakukan usaha pengolahan minuman dimasukkan dalam kelompok 82920.'),
(96, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1102', 'INDUSTRI MINUMAN BERALKOHOL HASIL FERMENTASI ANGGUR DAN HASIL PERTANIAN LAINNYA', '11020', 'INDUSTRI MINUMAN BERALKOHOL HASIL FERMENTASI ANGGUR DAN HASIL PERTANIAN LAINNYA', 'Kelompok ini mencakup industri pengolahan minuman secara fermentasi dengan bahan baku anggur, apel, buah-buahan lain atau hasil pertanian lainnya, seperti beras, sayuran, daun, batang dan akar (kecuali malt). Kegiatan yang tercakup adalah industri minuman anggur, sparkling wine, minuman anggur dari sari anggur, fermentasi tanpa destilasi minuman beralkohol, seperti sake, sari buah apel, perry, mead, minuman anggur dari buah lain dan minuman campuran yang mengandung alkohol, minuman anggur putih '),
(97, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1103', 'INDUSTRI MINUMAN BERALKOHOL HASIL FERMENTASI MALT DAN INDUSTRI MALT', '11031', 'INDUSTRI MINUMAN BERALKOHOL HASIL FERMENTASI MALT', 'Kelompok ini mencakup industri minuman beralkohol dari malt, seperti bir, ale, porter dan stout. Usaha pembotolan saja tanpa melakukan usaha pengolahan minuman dimasukkan dalam kelompok 82920. Termasuk Industri bir beralkohol rendah atau tanpa alkohol.'),
(98, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1103', 'INDUSTRI MINUMAN BERALKOHOL HASIL FERMENTASI MALT DAN INDUSTRI MALT', '11032', 'INDUSTRI MALT', 'Kelompok ini mencakup industri pembuatan malt (kecambah barley atau sereal lainnya yang dikeringkan).'),
(99, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1104', 'INDUSTRI MINUMAN RINGAN', '11040', 'INDUSTRI MINUMAN RINGAN', 'Kelompok ini mencakup usaha industri minuman yang tidak mengandung alkohol, kecuali bir dan anggur tanpa alkohol. Termasuk industri minuman ringan beraroma tanpa alkohol dan atau rasa manis, seperti lemonade, orangeade, cola, minuman buah, air tonik, limun, air soda, krim soda dan air anggur.'),
(100, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1105', 'INDUSTRI AIR MINUM DAN AIR MINERAL', '11050', 'INDUSTRI AIR MINUM DAN AIR MINERAL', 'Kelompok ini mencakup usaha pembuatan air minum dalam kemasan dan air mineral, air mineral alami, air demineral, termasuk industri air isi ulang.'),
(101, 'C', 'INDUSTRI PENGOLAHAN', '11', 'INDUSTRI MINUMAN', '110', 'INDUSTRI MINUMAN', '1109', 'INDUSTRI MINUMAN LAINNYA', '11090', 'INDUSTRI MINUMAN LAINNYA', 'Kelompok ini mencakup usaha Industri minuman lainnya, seperti minuman penyegar, nira, dan air tebu.'),
(102, 'C', 'INDUSTRI PENGOLAHAN', '12', 'INDUSTRI PENGOLAHAN TEMBAKAU', '120', 'INDUSTRI PENGOLAHAN TEMBAKAU', '1201', 'INDUSTRI ROKOK DAN PRODUK TEMBAKAU LAINNYA', '12011', 'INDUSTRI KRETEK', 'Kelompok ini mencakup usaha pembuatan kretek yang mengandung tembakau rajangan, krosok rajang, cengkeh rajang, dan tambahan bahan-bahan perisa, yang menghasilkan campuran beraroma khas, dilinting dengan berbagai bahan pembungkus (ambri/papir/tipping). Termasuk industri kretek tangan, kretek tangan filter, dan kretek mesin. Usaha pembungkusan/pengepakan rokok tanpa melakukan pembuatan rokok dimasukkan dalam kelompok 82920.'),
(103, 'C', 'INDUSTRI PENGOLAHAN', '12', 'INDUSTRI PENGOLAHAN TEMBAKAU', '120', 'INDUSTRI PENGOLAHAN TEMBAKAU', '1201', 'INDUSTRI ROKOK DAN PRODUK TEMBAKAU LAINNYA', '12012', 'INDUSTRI ROKOK PUTIH', 'Kelompok ini mencakup usaha pembuatan rokok putih yang tidak mengandung komponen cengkeh. Usaha pembungkusan/pengepakan rokok putih tanpa melakukan pembuatan rokok dimasukkan dalam kelompok 82920.'),
(104, 'C', 'INDUSTRI PENGOLAHAN', '12', 'INDUSTRI PENGOLAHAN TEMBAKAU', '120', 'INDUSTRI PENGOLAHAN TEMBAKAU', '1201', 'INDUSTRI ROKOK DAN PRODUK TEMBAKAU LAINNYA', '12019', 'INDUSTRI ROKOK LAINNYA', 'Kelompok ini mencakup usaha pembuatan rokok lainnya, selain kretek atau rokok putih, seperti cerutu, rokok kelembak menyan dan rokok klobot/kawung, tembakau iris (TIS), cerutek, dan hasil pengolahan tembakau lainnya (HPTL). Termasuk industri tembakau pipa, tembakau yang dikunyah dan tembakau sedot (snuff).'),
(105, 'C', 'INDUSTRI PENGOLAHAN', '12', 'INDUSTRI PENGOLAHAN TEMBAKAU', '120', 'INDUSTRI PENGOLAHAN TEMBAKAU', '1209', 'INDUSTRI PENGOLAHAN TEMBAKAU LAINNYA', '12091', 'INDUSTRI PENGERINGAN DAN PENGOLAHAN TEMBAKAU', 'Kelompok ini mencakup usaha pengeringan daun tembakau dengan pengasapan atau dengan cara lain termasuk juga usaha perajangan daun tembakau.'),
(106, 'C', 'INDUSTRI PENGOLAHAN', '12', 'INDUSTRI PENGOLAHAN TEMBAKAU', '120', 'INDUSTRI PENGOLAHAN TEMBAKAU', '1209', 'INDUSTRI PENGOLAHAN TEMBAKAU LAINNYA', '12099', 'INDUSTRI BUMBU ROKOK SERTA KELENGKAPAN ROKOK LAINNYA', 'Kelompok ini mencakup industri pengolahan tembakau yang belum diklasifikasikan ditempat lain, seperti industri homogenisasi atau rekonstitusi tembakau dan tembakau bersaus. Termasuk pembuatan bumbu rokok, serta kelengkapan rokok lainnya, seperti kelembak menyan, saus rokok/tembakau, uwur, klobot, kawung serta pembuatan filter.');
INSERT INTO `kd_kbli` (`idx`, `katid`, `kat`, `pokid`, `pok`, `golid`, `gol`, `subgolid`, `subgol`, `kelid`, `kel`, `desk`) VALUES
(107, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1311', 'INDUSTRI PENGOLAHAN DAN PEMINTALAN SERAT TEKSTIL', '13111', 'INDUSTRI PERSIAPAN SERAT TEKSTIL', 'Kelompok ini mencakup usaha persiapan serat tekstil, seperti reeling (pilin/menggulung) dan pencucian serat sutera, degreasasi (penghilangan lemak) dan karbonisasi wol dan pencelupan bulu domba, termasuk proses penyusunan dan penyisiran (carding atau combing) serat semua jenis binatang, tumbuhan dan serat buatan manusia.'),
(108, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1311', 'INDUSTRI PENGOLAHAN DAN PEMINTALAN SERAT TEKSTIL', '13112', 'INDUSTRI PEMINTALAN BENANG', 'Kelompok ini mencakup usaha pemintalan serat menjadi benang, kecuali benang jahit. Termasuk kegiatan penteksturan, penyimpulan, pelipatan dan pencucian benang rajutan filamen buatan atau sintetis dan industri benang rajutan dari bubur kayu.'),
(109, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1311', 'INDUSTRI PENGOLAHAN DAN PEMINTALAN SERAT TEKSTIL', '13113', 'INDUSTRI PEMINTALAN BENANG JAHIT', 'Kelompok ini mencakup usaha pembuatan benang jahit, baik dengan bahan baku serat maupun benang. termasuk kegiatan penteksturan, penyimpulan, pelipatan dan pencucian benang jahit.'),
(110, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1312', 'INDUSTRI PERTENUNAN TEKSTIL', '13121', 'INDUSTRI PERTENUNAN (BUKAN PERTENUNAN KARUNG GONI DAN KARUNG LAINNYA)', 'Kelompok ini mencakup usaha pertenunan, baik yang dibuat dengan alat gedogan, alat tenun bukan mesin (ATBM), alat tenun mesin (ATM) ataupun alat tenun lainnya, kecuali industri kain tenun ikat. Usaha pertenunan karung goni dan karung lainnya dimasukkan dalam kelompok 13995 atau 13996.'),
(111, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1312', 'INDUSTRI PERTENUNAN TEKSTIL', '13122', 'INDUSTRI KAIN TENUN IKAT', 'Kelompok ini mencakup usaha pembuatan kain tenun ikat dan usaha pewarnaan benang dengan cara mengikat terlebih dahulu.'),
(112, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1312', 'INDUSTRI PERTENUNAN TEKSTIL', '13123', 'INDUSTRI BULU TIRUAN TENUNAN', 'Kelompok ini mencakup usaha pembuatan bulu tiruan dengan penenunan.'),
(113, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1313', 'INDUSTRI PENYELESAIAN AKHIR TEKSTIL', '13131', 'INDUSTRI PENYEMPURNAAN BENANG', 'Kelompok ini mencakup usaha pengelantangan, pencelupan dan penyempurnaan lainnya untuk benang maupun benang jahit.'),
(114, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1313', 'INDUSTRI PENYELESAIAN AKHIR TEKSTIL', '13132', 'INDUSTRI PENYEMPURNAAN KAIN', 'Kelompok ini mencakup usaha pengelantangan, pencelupan dan penyempurnaan lainnya untuk kain.'),
(115, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1313', 'INDUSTRI PENYELESAIAN AKHIR TEKSTIL', '13133', 'INDUSTRI PENCETAKAN KAIN', 'Kelompok ini mencakup usaha pencetakan kain, termasuk juga pencetakan kain motif batik.'),
(116, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '131', 'INDUSTRI PEMINTALAN, PENENUNAN DAN PENYELESAIAN AKHIR TEKSTIL', '1313', 'INDUSTRI PENYELESAIAN AKHIR TEKSTIL', '13134', 'INDUSTRI BATIK', 'Kelompok ini mencakup usaha pembatikan dengan proses malam (lilin), baik yang dilakukan dengan tulis, cap maupun kombinasi antara cap dan tulis.'),
(117, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1391', 'INDUSTRI KAIN RAJUTAN DAN SULAMAN', '13911', 'INDUSTRI KAIN RAJUTAN', 'Kelompok ini mencakup usaha pembuatan kain yang dibuat dengan cara rajut ataupun renda.'),
(118, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1391', 'INDUSTRI KAIN RAJUTAN DAN SULAMAN', '13912', 'INDUSTRI KAIN SULAMAN/BORDIR', 'Kelompok ini mencakup usaha kain sulaman/bordir, baik yang dikerjakan dengan tangan maupun dengan mesin'),
(119, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1391', 'INDUSTRI KAIN RAJUTAN DAN SULAMAN', '13913', 'INDUSTRI BULU TIRUAN RAJUTAN', 'Kelompok ini mencakup usaha pembuatan bulu tiruan rajutan.'),
(120, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13921', 'INDUSTRI BARANG JADI TEKSTIL UNTUK KEPERLUAN RUMAH TANGGA', 'Kelompok ini mencakup usaha pembuatan barang-barang jadi tekstil, seperti selimut, seprei, taplak meja, sarung bantal, bed cover, gorden, handuk, selubung mobil dan selimut listrik dan lain-lain.'),
(121, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13922', 'INDUSTRI BARANG JADI TEKSTIL SULAMAN', 'Kelompok ini mencakup usaha barang jadi tekstil sulaman, baik yang dikerjakan dengan tangan maupun dengan mesin, seperti pakaian/barang jadi sulaman dan badge.'),
(122, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13923', 'INDUSTRI BANTAL DAN SEJENISNYA', 'Kelompok ini mencakup usaha pembuatan bantal dan sejenisnya, seperti bantal dan guling, selimut kapas, bantal kursi, kantong tidur dan lain-lain dari kapok, dakron dan sejenisnya.'),
(123, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13924', 'INDUSTRI BARANG JADI RAJUTAN DAN SULAMAN', 'Kelompok ini mencakup usaha pembuatan barang jadi rajutan, seperti kaos lampu, deker, bando.'),
(124, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13925', 'INDUSTRI KARUNG GONI', 'Kelompok ini mencakup usaha pembuatan karung goni.'),
(125, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13926', 'INDUSTRI KARUNG BUKAN GONI', 'Kelompok ini mencakup usaha pembuatan karung bagor (karung terigu/gula blacu) dan karung lainnya. Kecuali pembuatan karung plastik masuk dalam kelompok 22220.'),
(126, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1392', 'INDUSTRI PEMBUATAN BARANG TEKSTIL, BUKAN PAKAIAN JADI', '13929', 'INDUSTRI BARANG JADI TEKSTIL LAINNYA', 'Kelompok ini mencakup usaha pembuatan barang jadi tekstil lainnya, seperti layar, tenda, bendera, terpal, parasut, pelampung penyelamat dan lain-lain.'),
(127, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1393', 'INDUSTRI KARPET DAN PERMADANI', '13930', 'INDUSTRI KARPET DAN PERMADANI', 'Kelompok ini mencakup usaha pembuatan karpet dan permadani dan sejenisnya, yang terbuat dari serat, baik serat alam, sintetis maupun serat campuran, baik yang dikerjakan dengan proses tenun (woven), tufting, braiding, flocking dan needle punching. Termasuk industri penutup lantai dari lakan atau bulu kempa yang dibuat dengan jarum tenun. Karpet yang terbuat dari bahan-bahan gabus, karet atau plastik masing-masing dimasukkan dalam kelompok 16299, 22191 atau 22210. Kain alas lantai dengan lapisan '),
(128, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1394', 'INDUSTRI TALI DAN BARANG DARI TALI', '13941', 'INDUSTRI TALI', 'Kelompok ini mencakup usaha pembuatan berbagai macam tali, baik terbuat dari serat alam maupun serat sintetis atau serat campuran, seperti tali rami, tali goni (yute), tali sisal (agave), tali rafia dan tali nylon.'),
(129, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1394', 'INDUSTRI TALI DAN BARANG DARI TALI', '13942', 'INDUSTRI BARANG DARI TALI', 'Kelompok ini mencakup usaha pembuatan barang-barang dari tali, seperti jaring ikan, jala ikan, tali kapal, tali sepatu, sumbu kompor dan sumbu lampu, baik terbuat dari tali serat alam, tali serat sintetis atau tali serat campuran.'),
(130, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1399', 'INDUSTRI TEKSTIL LAINNYA YTDL', '13991', 'INDUSTRI KAIN PITA (NARROW FABRIC)', 'Kelompok ini mencakup usaha pembuatan kain pita, seperti kain pita, kain label, valcro, badges dan kain tulle.'),
(131, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1399', 'INDUSTRI TEKSTIL LAINNYA YTDL', '13992', 'INDUSTRI YANG MENGHASILKAN KAIN KEPERLUAN INDUSTRI', 'Kelompok ini mencakup usaha pembuatan kain dilapisi/ditutupi/diresapi dengan plastik atau karet dan selanjutnya digunakan untuk keperluan industri, seperti kain terpal, kain layar, kain tenda, kain payung, kain kanvas untuk melukis dan kulit imitasi dari media tekstil. Industri kulit imitasi dengan media selain tekstil dimasukkan dalam kelompok 15114.'),
(132, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1399', 'INDUSTRI TEKSTIL LAINNYA YTDL', '13993', 'INDUSTRI NON WOVEN (BUKAN TENUNAN)', 'Kelompok ini mencakup usaha industri kain yang dibuat tanpa dengan proses anyaman atau perajutan, seperti kain kempa, kain felting dan kain laken.'),
(133, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1399', 'INDUSTRI TEKSTIL LAINNYA YTDL', '13994', 'INDUSTRI KAIN BAN', 'Kelompok ini mencakup usaha pembuatan kain ban dari benang sintetik kekuatan tinggi, seperti kain ban dari nylon dan kain ban dari polyester.'),
(134, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1399', 'INDUSTRI TEKSTIL LAINNYA YTDL', '13995', 'INDUSTRI KAPUK', 'Kelompok ini mencakup usaha pengolahan kapuk.'),
(135, 'C', 'INDUSTRI PENGOLAHAN', '13', 'INDUSTRI TEKSTIL', '139', 'INDUSTRI TEKSTIL LAINNYA', '1399', 'INDUSTRI TEKSTIL LAINNYA YTDL', '13999', 'INDUSTRI TEKSTIL LAINNYA YTDL', 'Kelompok ini mencakup usaha industri tekstil lainnya yang belum/tidak tercakup dalam golongan industri tekstil manapun, seperti benang karet, benang logam dan pipa/selang kain dan lainnya.'),
(136, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '141', 'INDUSTRI PAKAIAN JADI DAN PERLENGKAPANNYA, BUKAN PAKAIAN JADI DARI KULIT BERBULU', '1411', 'INDUSTRI PAKAIAN JADI (BUKAN PENJAHITAN DAN PEMBUATAN PAKAIAN)', '14111', 'INDUSTRI PAKAIAN JADI (KONVEKSI) DARI TEKSTIL', 'Kelompok ini mencakup usaha pembuatan pakaian jadi (konveksi) dari tekstil/kain (tenun maupun rajutan) dengan cara memotong dan menjahit sehingga siap dipakai, seperti kemeja, celana, kebaya, blus, rok, baju bayi, pakaian tari dan pakaian olahraga, baik dari kain tenun maupun kain rajut yang dijahit.'),
(137, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '141', 'INDUSTRI PAKAIAN JADI DAN PERLENGKAPANNYA, BUKAN PAKAIAN JADI DARI KULIT BERBULU', '1411', 'INDUSTRI PAKAIAN JADI (BUKAN PENJAHITAN DAN PEMBUATAN PAKAIAN)', '14112', 'INDUSTRI PAKAIAN JADI (KONVEKSI) DARI KULIT', 'Kelompok ini mencakup usaha pembuatan pakaian jadi (konveksi) dari kulit atau kulit imitasi, dengan cara memotong dan menjahit sehingga siap pakai, seperti jaket, mantel, rompi, celana dan rok. Termasuk pembuatan aksesori pakaian dari kulit seperti welder`s leather aprons atau pakaian kerja tukang las dari kulit.'),
(138, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '141', 'INDUSTRI PAKAIAN JADI DAN PERLENGKAPANNYA, BUKAN PAKAIAN JADI DARI KULIT BERBULU', '1412', 'PENJAHITAN DAN PEMBUATAN PAKAIAN SESUAI PESANAN', '14120', 'PENJAHITAN DAN PEMBUATAN PAKAIAN SESUAI PESANAN', 'Kelompok ini mencakup usaha penjahitan dan pembuatan pakaian sesuai pesanan yang melayani masyarakat umum dengan tujuan komersil.'),
(139, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '141', 'INDUSTRI PAKAIAN JADI DAN PERLENGKAPANNYA, BUKAN PAKAIAN JADI DARI KULIT BERBULU', '1413', 'INDUSTRI PERLENGKAPAN PAKAIAN YANG UTAMANYA TERBUAT DARI TEKSTIL', '14131', 'INDUSTRI PERLENGKAPAN PAKAIAN DARI TEKSTIL', 'Kelompok ini mencakup usaha pembuatan perlengkapan pakaian jadi (konveksi) tekstil dan dari kain dengan cara memotong dan menjahit sehingga siap dipakai, seperti topi, peci, dasi, sarung tangan, mukena, selendang, kerudung, ikat pinggang, syal, bando, dasi tuksedo dan lain-lain, baik dari kain tenun maupun kain rajut yang dijahit. Termasuk industri alas kaki dari bahan kain tanpa sol dan bagian-bagian dari produk yang disebutkan sebelumnya.'),
(140, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '141', 'INDUSTRI PAKAIAN JADI DAN PERLENGKAPANNYA, BUKAN PAKAIAN JADI DARI KULIT BERBULU', '1413', 'INDUSTRI PERLENGKAPAN PAKAIAN YANG UTAMANYA TERBUAT DARI TEKSTIL', '14132', 'INDUSTRI PERLENGKAPAN PAKAIAN DARI KULIT', 'Kelompok ini mencakup usaha pembuatan perlengkapan pakaian jadi dari kulit atau kulit imitasi, dengan cara memotong dan menjahit sehingga siap pakai, seperti topi, sarung tangan, ikat pinggang dan lain-lain. Termasuk industri penutup kepala dari kulit berbulu dan bagian-bagian dari produk yang disebutkan sebelumnya.'),
(141, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '142', 'INDUSTRI PAKAIAN JADI DAN BARANG DARI KULIT BERBULU', '1420', 'INDUSTRI PAKAIAN JADI DAN BARANG DARI KULIT BERBULU', '14200', 'INDUSTRI PAKAIAN JADI DAN BARANG DARI KULIT BERBULU', 'Kelompok ini mencakup usaha pembuatan pakaian jadi/barang jadi dari kulit berbulu dan atau perlengkapannya, seperti mantel berbulu, berbagai barang dari kulit berbulu, misalnya gambar, tikar, keset dan barang lain dari kulit berbulu, seperti permadani, kain kilap industri.'),
(142, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '143', 'INDUSTRI PAKAIAN JADI RAJUTAN DAN SULAMAN/BORDIR', '1430', 'INDUSTRI PAKAIAN JADI RAJUTAN DAN SULAMAN/BORDIR', '14301', 'INDUSTRI PAKAIAN JADI RAJUTAN', 'Kelompok ini mencakup usaha pembuatan pakaian jadi, seperti sweater, kardigan, baju kaos, mantel, dan barang sejenisnya, termasuk topi yang dibuat dengan cara dirajut ataupun renda, kecuali industri rajutan kaos kaki.'),
(143, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '143', 'INDUSTRI PAKAIAN JADI RAJUTAN DAN SULAMAN/BORDIR', '1430', 'INDUSTRI PAKAIAN JADI RAJUTAN DAN SULAMAN/BORDIR', '14302', 'INDUSTRI PAKAIAN JADI SULAMAN/BORDIR', 'Kelompok ini mencakup usaha pakaian jadi sulaman, baik yang dikerjakan dengan tangan maupun dengan mesin.'),
(144, 'C', 'INDUSTRI PENGOLAHAN', '14', 'INDUSTRI PAKAIAN JADI', '143', 'INDUSTRI PAKAIAN JADI RAJUTAN DAN SULAMAN/BORDIR', '1430', 'INDUSTRI PAKAIAN JADI RAJUTAN DAN SULAMAN/BORDIR', '14303', 'INDUSTRI RAJUTAN KAOS KAKI DAN SEJENISNYA', 'Kelompok ini mencakup usaha pembuatan kaos kaki yang dibuat dengan cara rajut ataupun renda, seperti kaos kaki, termasuk kaos kaki, stocking, pantyhose.'),
(145, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1511', 'INDUSTRI KULIT DAN KULIT BUATAN, TERMASUK PENCELUPAN KULIT BERBULU', '15111', 'INDUSTRI PENGAWETAN KULIT', 'Kelompok ini mencakup usaha pengawetan kulit yang berasal dari hewan besar, hewan kecil, reptil, ikan dan hewan lainnya, baik yang dilakukan dengan pengeringan, penggaraman, maupun pengasaman (pikel), seperti kulit hewan besar (sapi, kerbau), kulit hewan kecil (domba, kambing), kulit reptil (buaya, ular, biawak), kulit ikan (ikan pari,hiu/cucut, kakap, belut) dan kulit hewan lainnya.'),
(146, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1511', 'INDUSTRI KULIT DAN KULIT BUATAN, TERMASUK PENCELUPAN KULIT BERBULU', '15112', 'INDUSTRI PENYAMAKAN KULIT', 'Kelompok ini mencakup usaha penyamakan kulit yang berasal dari ternak besar (sapi, kerbau), ternak kecil (domba, kambing), reptil (buaya, ular, biawak), ikan (ikan pari, hiu cucut, kakap, belut) dan hewan lainnya yang dimasak dengan chrome nabati, sintetis, samak minyak dan samak kombinasi menjadi kulit tersamak, seperti wet blue, crust, sol, vache raam, kulit box, kulit beludru, kulit gelase dan kulit hiasan, kulit berbulu, kulit laminasi, kulit patent, kulit jaket, kulit sarung tangan, kulit c'),
(147, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1511', 'INDUSTRI KULIT DAN KULIT BUATAN, TERMASUK PENCELUPAN KULIT BERBULU', '15113', 'INDUSTRI PENCELUPAN KULIT BULU', 'Kelompok ini mencakup usaha pemberian warna atau pencelupan pada kulit bulu yang digunakan pada pakaian jadi tekstil.'),
(148, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1511', 'INDUSTRI KULIT DAN KULIT BUATAN, TERMASUK PENCELUPAN KULIT BERBULU', '15114', 'INDUSTRI KULIT BUATAN/IMITASI', 'Kelompok ini mencakup usaha pembuatan kulit buatan atau kulit imitasi yang berbahan dasar kulit asli. Industri kulit buatan/imitasi dengan media tekstil dimasukkan dalam kelompok 13992.'),
(149, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1512', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN, KOPER, TAS TANGAN DAN SEJENISNYA, PELANA DAN ALAT PENGEKANG (HARNESS)', '15121', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN UNTUK KEPERLUAN PRIBADI', 'Kelompok ini mencakup usaha pembuatan barang-barang dari kulit dan kulit buatan atau bahan lain seperti plastik, tekstil, serat yang divulkanisir atau paperboard untuk keperluan pribadi, seperti kopor, ransel, tas, dompet, kotak rias, sarung senjata, tempat kaca mata dan tali jam. Termasuk industri tali sepatu kulit.'),
(150, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1512', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN, KOPER, TAS TANGAN DAN SEJENISNYA, PELANA DAN ALAT PENGEKANG (HARNESS)', '15122', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN UNTUK KEPERLUAN TEKNIK/INDUSTRI', 'Kelompok ini mencakup usaha pembuatan barang-barang dari kulit dan kulit buatan untuk keperluan teknik/industri, seperti klep, packing, rem pickers, sarung tangan kerja, kulit pompa, kulit ban mesin (belt), kulit apron dan sisir kulit pada mesin (combing leather).'),
(151, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1512', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN, KOPER, TAS TANGAN DAN SEJENISNYA, PELANA DAN ALAT PENGEKANG (HARNESS)', '15123', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN UNTUK KEPERLUAN HEWAN ', 'Kelompok ini mencakup usaha pembuatan barang-barang dari kulit dan kulit buatan untuk keperluan hewan, seperti ikat leher hewan, tali kekang hewan, pelana hewan, brongsong mulut hewan, cambuk dan sepatu hewan.'),
(152, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '151', 'INDUSTRI KULIT DAN BARANG DARI KULIT, TERMASUK KULIT BUATAN', '1512', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN, KOPER, TAS TANGAN DAN SEJENISNYA, PELANA DAN ALAT PENGEKANG (HARNESS)', '15129', 'INDUSTRI BARANG DARI KULIT DAN KULIT BUATAN UNTUK KEPERLUAN LAINNYA', 'Kelompok ini mencakup usaha pembuatan barang-barang dari kulit dan kulit buatan untuk keperluan yang belum terliput dalam kelompok 15121 sampai dengan 15123, seperti jok, sabuk pengaman, alat pengepak dan kerajinan tatah sungging (hiasan, wayang dan kap lampu) dan lain-lain.'),
(153, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '152', 'INDUSTRI ALAS KAKI', '1520', 'INDUSTRI ALAS KAKI', '15201', 'INDUSTRI ALAS KAKI UNTUK KEPERLUAN SEHARI-HARI', 'Kelompok ini mencakup usaha pembuatan alas kaki keperluan sehari-hari dari kulit dan kulit buatan, karet, kanvas dan kayu, seperti sepatu harian, sepatu santai (casual shoes), sepatu sandal, sandal kelom dan selop. Termasuk juga usaha pembuatan bagian-bagian dari alas kaki tersebut, seperti atasan, sol dalam, sol luar, penguat depan, penguat tengah, penguat belakang, lapisan dan aksesori dari kulit dan kulit buatan.'),
(154, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '152', 'INDUSTRI ALAS KAKI', '1520', 'INDUSTRI ALAS KAKI', '15202', 'INDUSTRI SEPATU OLAHRAGA', 'Kelompok ini mencakup usaha pembuatan sepatu untuk olahraga dari kulit dan kulit buatan, karet dan kanvas, seperti sepatu sepak bola, sepatu atletik, sepatu senam, sepatu jogging dan sepatu ballet. Termasuk juga usaha pembuatan bagian bagian dari sepatu olahraga tersebut, meliputi atasan, sol luar, sol dalam, lapisan dan aksesori'),
(155, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '152', 'INDUSTRI ALAS KAKI', '1520', 'INDUSTRI ALAS KAKI', '15203', 'INDUSTRI SEPATU TEKNIK LAPANGAN/KEPERLUAN INDUSTRI', 'Kelompok ini mencakup usaha pembuatan sepatu termasuk pembuatan bagian-bagian dari sepatu untuk keperluan teknik lapangan/industri dari kulit, kulit buatan, karet dan plastik, seperti sepatu tahan kimia, sepatu tahan panas, sepatu pengaman.'),
(156, 'C', 'INDUSTRI PENGOLAHAN', '15', 'INDUSTRI KULIT, BARANG DARI KULIT DAN ALAS KAKI', '152', 'INDUSTRI ALAS KAKI', '1520', 'INDUSTRI ALAS KAKI', '15209', 'INDUSTRI ALAS KAKI LAINNYA', 'Kelompok ini mencakup usaha pembuatan alas kaki lainnya yang belum termasuk golongan manapun, seperti sepatu kesehatan dan sepatu lainnya, misalnya sepatu dari gedebog (pelepah batang pisang) dan eceng gondok. Termasuk industri gaiter, legging dan barang sejenisnya.'),
(157, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '161', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '1610', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '16101', 'INDUSTRI PENGGERGAJIAN KAYU', 'Kelompok ini mencakup usaha penggergajian, penyerutan, pengirisan, pengulitan dan pemotongan kayu gelondongan menjadi balok, kaso (usuk), reng, papan dan sebagainya. Termasuk industri kayu untuk bantalan rel kereta, kayu untuk lantai dan wol kayu, tepung kayu, irisan dan partikel kayu.'),
(158, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '161', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '1610', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '16102', 'INDUSTRI PENGAWETAN KAYU', 'Kelompok ini mencakup usaha pengawetan kayu dengan cara pengeringan kayu, pengolahan kimia dan perendaman kayu dengan bahan pengawet atau bahan lainnya.'),
(159, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '161', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '1610', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '16103', 'INDUSTRI PENGAWETAN ROTAN, BAMBU DAN SEJENISNYA', 'Kelompok ini mencakup usaha pengawetan rotan, bambu dan sejenisnya.'),
(160, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '161', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '1610', 'INDUSTRI PENGGERGAJIAN DAN PENGAWETAN KAYU, ROTAN, BAMBU DAN SEJENISNYA', '16104', 'INDUSTRI PENGOLAHAN ROTAN', 'Kelompok ini mencakup usaha pengolahan rotan menjadi bahan setengah jadi, seperti rotan poles, hati rotan dan kulit rotan.'),
(161, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1621', 'INDUSTRI KAYU LAPIS, VENEER DAN SEJENISNYA', '16211', 'INDUSTRI KAYU LAPIS', 'Kelompok ini mencakup usaha pembuatan kayu lapis biasa, seperti kayu lapis tripleks, multipleks, kayu lapis interior, eksterior dan sejenisnya. Termasuk juga kayu lapis konstruksi, seperti kayu lapis cetak beton, kayu lapis tahan air dan sejenisnya.'),
(162, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1621', 'INDUSTRI KAYU LAPIS, VENEER DAN SEJENISNYA', '16212', 'INDUSTRI KAYU LAPIS LAMINASI, TERMASUK DECORATIVE PLYWOOD', 'Kelompok ini mencakup usaha pembuatan kayu lapis yang dilaminasi, seperti teak wood, rose wood, polyester plywood dan sejenisnya. Termasuk juga bambu lapis yang dilaminasi.'),
(163, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1621', 'INDUSTRI KAYU LAPIS, VENEER DAN SEJENISNYA', '16213', 'INDUSTRI PANEL KAYU LAINNYA', 'Kelompok ini mencakup usaha pembuatan panel kayu lainnya, seperti block board, particle board, chip board, lamin board, fibre board, Medium Density Fibreboard (MDF) dan sejenisnya.'),
(164, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1621', 'INDUSTRI KAYU LAPIS, VENEER DAN SEJENISNYA', '16214', 'INDUSTRI VENEER', 'Kelompok ini mencakup usaha pembuatan serutan pelapis (veneer) dengan cara pengupasan (rotary), penyayatan (slicer) dan sejenisnya.'),
(165, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1622', 'INDUSTRI BARANG BANGUNAN DARI KAYU', '16221', 'INDUSTRI BARANG BANGUNAN DARI KAYU', 'Kelompok ini mencakup usaha pengerjaan kayu untuk bahan bangunan, seperti dowels, moulding, kusen, lis, daun pintu/jendela, tiang penopang yang dibuat dari kayu, lantai/lantai dari papan yang bergambar (lantai hias) atau kepingan atau potongan lantai dan lainnya yang terpasang menjadi panel, langit-langit, atap, kerei, tangga dari kayu dan susurannya, manik-manik dari kayu dan papan penghias tembok dan papan nama dan pengerjaan kayu untuk bahan bangunan lainnya. Termasuk industri rumah bergerak '),
(166, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1622', 'INDUSTRI BARANG BANGUNAN DARI KAYU', '16222', 'INDUSTRI BANGUNAN PRAFABRIKASI DARI KAYU', 'Kelompok ini mencakup usaha pengerjaan kayu untuk bangunan prafabrikasi.'),
(167, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1623', 'INDUSTRI WADAH DARI KAYU', '16230', 'INDUSTRI WADAH DARI KAYU', 'Kelompok ini mencakup usaha pembuatan segala macam wadah atau peti/kotak dari kayu untuk pengemasan, seperti kotak kemas, boks, peti kayu, drum kayu dan kemasan sejenisnya dari kayu; palet (pallets), kotak palet dan papan muat dari kayu lainnya; barel, tong, ember dan produk dari kayu lainnya; dan gulungan kawat dari kayu.'),
(168, 'C', 'INDUSTRI PENGOLAHAN', '16', 'INDUSTRI KAYU, BARANG DARI KAYU DAN GABUS (TIDAK TERMASUK FURNITUR) DAN BARANG ANYAMAN DARI BAMBU, ROTAN DAN SEJENISNYA', '162', 'INDUSTRI BARANG DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENIS LAINNYA', '1629', 'INDUSTRI BARANG LAINNYA DARI KAYU; INDUSTRI BARANG DARI GABUS DAN BARANG ANYAMAN DARI JERAMI, ROTAN, BAMBU DAN SEJENISNYA', '16291', 'INDUSTRI BARANG ANYAMAN DARI ROTAN DAN BAMBU', 'Kelompok ini mencakup usaha pembuatan macam-macam tikar, webbing, lampit, tas, topi, tampah, kukusan, bakul, kipas, tatakan, bilik/gedek dan sejenisnya yang bahan utamanya dari rotan atau bambu.'),
